import 'package:flutter/material.dart';
import 'package:flutter_test/flutter_test.dart';
import 'package:integration_test/integration_test.dart';
import 'package:student_object_box/app/app.dart';
import 'package:student_object_box/data%20source/remote_data_source/student_data_source.dart';

void main() {
  IntegrationTestWidgetsFlutterBinding.ensureInitialized();
  testWidgets('Add two numbers and check the results',
      (WidgetTester tester) async {
    await tester.pumpWidget(
      const MaterialApp(
        home: MyApp(),
      ),
    );

    Finder txtFirst = find.byKey(const ValueKey('txtFirst'));
    await tester.enterText(txtFirst, '10');
    Finder txtSecond = find.byKey(const ValueKey('txtSecond'));
    await tester.enterText(txtSecond, '20');

    Finder btnAdd = find.byKey(const ValueKey('btnAdd'));
    await tester.tap(btnAdd);
    await tester.pumpAndSettle();
    expect(find.text('Result : 30'), findsOneWidget);
  });

  test('Login test', () async {
    bool expectedResult = true;
    String username = "karma21";
    String password = "karmagrg";
    bool actualResult =
        await StudentRemoteDataSource().loginStudent(username, password);

    expect(actualResult, expectedResult);
  });
}
