import 'package:dio/dio.dart';


class CategoryAPI {
  Future<bool> addCategory(Category category) async {
    bool isCategoryAdded = false;
    Dio dio = HttpServices().getDioInstance();
    try {
      // dio ko response --> server le dine
      Response response = await dio.post(categoryUrl, data: category.toJson());
      // response.data
      final CategoryResponse categoryResponseData =
          CategoryResponse.fromJson(response.data);
      if (categoryResponseData.success == true) {
        isCategoryAdded = true;
        return isCategoryAdded;
      }
    } catch (exception) {
      print(exception);
    }
    return isCategoryAdded;
  }

  Future<List<DropdownCategory?>> getAllDropdownCategoryList() async {
    List<DropdownCategory?> dropDownCategoryList = [];
    Dio dio = HttpServices().getDioInstance();
    try {
      // dio ko response --> server le dine
      Response response = await dio.get(categoryUrl);
      // response.data
      final CategoryResponse categoryResponseData =
          CategoryResponse.fromJson(response.data);
      if (categoryResponseData.success == true) {
        for (var singleCategoryData in categoryResponseData.data!)
          dropDownCategoryList.add(
              DropdownCategory(singleCategoryData.id, singleCategoryData.name));
        return dropDownCategoryList;
      }
    } catch (exception) {
      print(exception);
    }
    return dropDownCategoryList;
  }
}
