import 'dart:io';

import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:image_picker/image_picker.dart';
import 'package:image_pickers/Contraints.dart';

class ImagePickerScreen extends StatefulWidget {
  static String route = "ImagePickerScreen";
  const ImagePickerScreen({Key? key}) : super(key: key);

  @override
  _ImagePickerScreenState createState() => _ImagePickerScreenState();
}

class _ImagePickerScreenState extends State<ImagePickerScreen>
    with SingleTickerProviderStateMixin {
  File? uploadImage;

  Future loadImage(ImageSource imageSource) async {
    try {
      final image = await ImagePicker().pickImage(source: imageSource);
      if (image != null) {
        setState(() {
          uploadImage = File(image.path);
        });
      } else {
        return;
      }
    } catch (e) {
      print("Failed to upload Image");
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text("Image Picker App"),
        centerTitle: true,
        backgroundColor: Colors.purple,
      ),
      body: Column(
        crossAxisAlignment: CrossAxisAlignment.center,
        children: [
          // App ko logo
          Row(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              Hero(
                tag: KLR_TAG,
                child: Image(
                  image: AssetImage("images/logo.jpg"),
                  height: 70.0,
                  width: 170.0,
                  fit: BoxFit.cover,
                ),
              ),
              Text(
                "KLR",
                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 80.0),
              ),
            ],
          ),

          Divider(
            thickness: 5.0,
            color: Colors.black,
          ),
          // display image --> image box
          showUploadedImage(uploadImage),
          // from camera --> button
          ElevatedButton.icon(
            onPressed: () {
              loadImage(ImageSource.camera); // from camera
            },
            icon: Icon(Icons.camera),
            label: Text("Open Camera"),
          ),
          // from gallery --> button
          ElevatedButton.icon(
            onPressed: () {
              // open gallery --> load_image
              loadImage(FROM_GALLERY); // from gallery
            },
            icon: Icon(Icons.photo),
            label: Text("Open Gallery"),
          ),
        ],
      ),
    );
  }

  Widget showUploadedImage(File? pickedImageFile) {
    return Card(
      shape: RoundedRectangleBorder(
        borderRadius: BorderRadius.circular(20.0),
        side: BorderSide(color: Colors.purple, width: 3.0),
      ),
      color: Colors.red,
      child: ClipRRect(
        borderRadius: BorderRadius.circular(20.0),
        child: SingleChildScrollView(
          physics: BouncingScrollPhysics(),
          child: Column(
            children: [
              pickedImageFile == null
                  ? Image.asset("images/upload.png")
                  : Image.file(
                      pickedImageFile,
                      width: double.infinity,
                      height: MediaQuery.of(context).size.height * 0.4,
                      fit: BoxFit.cover,
                    )
            ],
          ),
        ),
      ),
    );
  }

}
