import 'package:flutter/material.dart';
import 'package:foodex_app/app/theme/constants.dart';
import 'package:foodex_app/widgets/export_widgets.dart';
import 'package:line_awesome_flutter/line_awesome_flutter.dart';

class ProfileScreen extends StatefulWidget {
  const ProfileScreen({super.key});

  @override
  State<ProfileScreen> createState() => _ProfileScreenState();
}

class _ProfileScreenState extends State<ProfileScreen> {
  @override
  Widget build(BuildContext context) {
    var isDark = MediaQuery.of(context).platformBrightness == Brightness.dark;

    return Scaffold(
        appBar: AppBar(
          leading: IconButton(
            onPressed: () {},
            icon: const Icon(LineAwesomeIcons.angle_left),
          ),
          title: const Text('Profile'),
          actions: [
            IconButton(
                onPressed: () {},
                icon: Icon(
                    isDark ? LineAwesomeIcons.sun : LineAwesomeIcons.moon)),
          ],
        ),
        body: Container(
          padding: const EdgeInsets.all(20),
          child: Column(children: [
            SizedBox(
              width: 120,
              height: 120,
              child: ClipRRect(
                borderRadius: BorderRadius.circular(100),
                child: Image.network(
                    'https://res.cloudinary.com/dymp3ozdm/image/upload/v1676054258/foodex/user/profile_1_jhrgps.jpg'),
              ),
            ),
            const SizedBox(height: 20),
            BigText(
              text: "Karma Gurung",
              size: 24,
            ),
            SmallText(
              text: "Karma@gmail.com",
              size: 16,
            ),
            const SizedBox(height: 20),
            SizedBox(
              width: 200,
              child: ElevatedButton(
                onPressed: () {},
                style: ElevatedButton.styleFrom(
                    backgroundColor: AppColor.kSecondaryColor,
                    side: BorderSide.none,
                    shape: const StadiumBorder()),
                child: const Text('Edit Profile'),
              ),
            ),
            const SizedBox(height: 20),
            const Divider(),
            ProfileMenuWidget(
              title: "Profile",
              icon: LineAwesomeIcons.user_circle,
              onPress: () {},
            ),
            ProfileMenuWidget(
              title: "Address",
              icon: LineAwesomeIcons.map_marker,
              onPress: () {},
            ),
            ProfileMenuWidget(
              title: "favorite",
              bgColor: AppColor.kErrorColor,
              iconColor: AppColor.kErrorColor,
              icon: LineAwesomeIcons.heart,
              onPress: () {},
            ),
            ProfileMenuWidget(
              title: "Order History",
              // iconColor: AppColor.kErrorColor,
              icon: LineAwesomeIcons.history,
              onPress: () {},
            ),
            ProfileMenuWidget(
              title: "Dark Mode",
              iconColor: isDark ? Colors.white : Colors.black,
              icon: isDark ? LineAwesomeIcons.sun : LineAwesomeIcons.moon,
              endIcon: false,
              onPress: () {},
            ),
            ProfileMenuWidget(
              title: "Logout",
              bgColor: AppColor.kErrorColor,
              icon: LineAwesomeIcons.alternate_sign_out,
              iconColor: AppColor.kErrorColor,
              textColor: Colors.red,
              endIcon: false,
               onPressed: () async {
                SharedPreferences pref = await SharedPreferences.getInstance();
                showSnackbar(context, "Logout Successfully!", Colors.orange);
                pref.clear();
                Navigator.of(context).pushReplacement(
                  MaterialPageRoute(
                    builder: (_) => const LoginScreen(),
                  ),
                );
              },
            ),
          ]),
        ));
  }
}
