// ignore_for_file: public_member_api_docs, sort_constructors_first
import 'package:flutter/material.dart';
import 'package:line_awesome_flutter/line_awesome_flutter.dart';

import 'package:foodex_app/app/theme/constants.dart';
import 'package:foodex_app/widgets/export_widgets.dart';

class ProfileMenuWidget extends StatelessWidget {
  const ProfileMenuWidget({
    Key? key,
    required this.title,
    required this.icon,
    this.iconColor = AppColor.kPrimaryColor,
    this.bgColor = AppColor.kPrimaryColor,
    required this.onPress,
    this.endIcon = true,
    this.textColor,
  }) : super(key: key);
  final String title;
  final IconData icon;
  final Color iconColor;
  final Color bgColor;
  final VoidCallback onPress;
  final bool endIcon;
  final Color? textColor;

  @override
  Widget build(BuildContext context) {
    return ListTile(
      leading: Container(
        on
        width: 40,
        height: 40,
        decoration: BoxDecoration(
            color: bgColor == bgColor.withOpacity(0.1)
                ? bgColor.withOpacity(0.1)
                : bgColor.withOpacity(0.1),
            borderRadius: BorderRadius.circular(10)),
        child: Icon(
          icon,
          color: iconColor == AppColor.kPrimaryColor
              ? AppColor.kPrimaryColor
              : iconColor,
          size: 20,
        ),
      ),
      title: SmallText(
        text: title,
        size: 16,
        color: textColor ?? Theme.of(context).colorScheme.onPrimary,
      ),
      trailing: endIcon
          ? Container(
              height: 30,
              width: 30,
              decoration: BoxDecoration(
                  color: Colors.grey.withOpacity(0.1),
                  borderRadius: BorderRadius.circular(100)),
              child: const Icon(
                LineAwesomeIcons.angle_right,
                color: Colors.grey,
                size: 20.0,
              ),
            )
          : null,
    );
  }
}
