import 'package:flutter/material.dart';
import 'package:foodex_app/constant/constants.dart';
import 'package:foodex_app/utils/dimension.dart';

import '../../widgets/widgets.dart';

class FoodListScreen extends StatefulWidget {
  const FoodListScreen({super.key});

  @override
  State<FoodListScreen> createState() => SLIDER();
}

class SLIDER extends State<FoodListScreen> {
  PageController pageController = PageController(viewportFraction: 0.85);

  var _currPageValue = 0.0;
  final double _scaleFactor = 0.8;
  final double _height = Dimensions.pageViewContainer;

  @override
  void initState() {
    super.initState();
    pageController.addListener(() {
      setState(() {
        _currPageValue = pageController.page!;
      });
    });
  }

  // @override
  // void dispose() {
  //   pageController.dispose();
  // }

  @override
  Widget build(BuildContext context) {
    return Column(
      children: [
        // ======================== Slide Section Start ========================
        // -------------------------Title --------------------------------------
        Container(
          margin: EdgeInsets.only(left: Dimensions.width30),
          alignment: Alignment.topLeft,
          child: BigText(text: "Today Specials"),
        ),
        SizedBox(
          height: Dimensions.height10,
        ),
        // -------------------------- Slide widget calling ---------------------
        SizedBox(
          height: Dimensions.pageView,
          child: PageView.builder(
              controller: pageController,
              itemCount: 5,
              itemBuilder: (context, position) {
                return _buildPageItem(position);
              }),
        ),
        SizedBox(
          height: Dimensions.height10,
        ),
        // ============================ Slider Section End =====================

        // ======================== Catagories Section Start ===================
        // ------------------------- Title -------------------------------------
        Container(
          margin: EdgeInsets.only(left: Dimensions.width30),
          alignment: Alignment.topLeft,
          child: BigText(text: "Catagories"),
        ),
        SizedBox(
          height: Dimensions.height10,
        ),
        // ----------------------- Catagories Widget Called --------------------
        _foodList(),
        SizedBox(
          height: Dimensions.height10,
        ),
        // ======================== Catagories Section End =====================

        // ====================== Popular Section Start ========================
        // ------------------------ Title --------------------------------------
        Container(
          margin: EdgeInsets.only(left: Dimensions.width30),
          child: Row(
            crossAxisAlignment: CrossAxisAlignment.end,
            children: [
              BigText(text: "Popular"),
              SizedBox(
                width: Dimensions.width10,
              ),
              Container(
                margin: const EdgeInsets.only(bottom: 3),
                child: BigText(
                  text: '.',
                  color: Colors.black26,
                ),
              ),
              SizedBox(
                width: Dimensions.width10,
              ),
              // ------------------------ Sub Title ----------------------------
              Container(
                margin: const EdgeInsets.only(bottom: 2),
                child: SmallText(text: "Food pairing"),
              ),
              SizedBox(
                width: Dimensions.width10,
              )
            ],
          ),
        ),
        //-------------------- List of Food and Images -------------------------
        ListView.builder(
          physics: const NeverScrollableScrollPhysics(),
          shrinkWrap: true,
          itemCount: 10,
          itemBuilder: (BuildContext context, index) {
            return Container(
              margin: EdgeInsets.only(
                  left: Dimensions.width20,
                  right: Dimensions.width20,
                  bottom: Dimensions.height10),
              child: Row(children: [
                //----------------------- Image Section ------------------------
                Container(
                  width: Dimensions.listViewImgSize,
                  height: Dimensions.listViewImgSize,
                  decoration: BoxDecoration(
                      borderRadius: BorderRadius.circular(Dimensions.radius20),
                      color: AppColor.,
                      image: const DecorationImage(
                          fit: BoxFit.cover,
                          image: AssetImage("assets/images/food.jpg"))),
                ),

                //------------------- Text and icon section --------------------
                Expanded(
                  child: Container(
                    height: Dimensions.listViewTextContSize,
                    decoration: BoxDecoration(
                        borderRadius: BorderRadius.only(
                            topRight: Radius.circular(Dimensions.radius20),
                            bottomRight: Radius.circular(Dimensions.radius20)),
                        color: Colors.white),
                    child: Padding(
                      padding: EdgeInsets.only(
                          left: Dimensions.width10, right: Dimensions.width10),
                      child: Column(
                        crossAxisAlignment: CrossAxisAlignment.start,
                        mainAxisAlignment: MainAxisAlignment.center,
                        children: [
                          BigText(
                              text: 'Crunchy Fried chip of Foodex restaurant'),
                          SizedBox(
                            height: Dimensions.height10,
                          ),
                          SmallText(text: 'Made with great potato of Mustang'),
                          SizedBox(
                            height: Dimensions.height10,
                          ),

                          // ------------------- Rating Section ----------------
                          Wrap(
                              children: List.generate(
                                  5,
                                  (index) => Icon(
                                        Icons.star,
                                        color: AppColor.kSecondaryColor,
                                        size: Dimensions.inconSize24,
                                      ))),
                          SizedBox(
                            height: Dimensions.height10,
                          ),

                          // -------------- Bottom Icons Section ---------------
                          Row(
                            mainAxisAlignment: MainAxisAlignment.spaceBetween,
                            children: const [
                              IconTextWidget(
                                  icon: Icons.thumb_up_outlined,
                                  Text: 'Add to cart',
                                  iconColor: AppColor.kButtonColor),
                              IconTextWidget(
                                icon: Icons.location_on,
                                Text: '1.7km',
                                iconColor: Colors.blue,
                              ),
                              IconTextWidget(
                                  icon: Icons.access_time_rounded,
                                  Text: '32 min',
                                  iconColor: AppColor.kPrimaryColor),
                            ],
                          ),
                          // ================= Popular Section End =============
                        ],
                      ),
                    ),
                  ),
                )
              ]),
            );
          },
        ),
      ],
    );
  }

// ======================== SLIDER Widget SECTION ==============================
  Widget _buildPageItem(int index) {
    // ------------------- logic to for customize sliding ----------------------
    Matrix4 matrix = Matrix4.identity();
    if (index == _currPageValue.floor()) {
      var currScale = 1 - (_currPageValue - index) * (1 - _scaleFactor);
      var currTrans = _height * (1 - currScale) / 2;
      matrix = Matrix4.diagonal3Values(1, currScale, 1)
        ..setTranslationRaw(0, currTrans, 0);
    } else if (index == _currPageValue.floor() + 1) {
      var currScale =
          _scaleFactor + (_currPageValue - index + 1) * (1 - _scaleFactor);
      var currTrans = _height * (1 - currScale) / 2;
      matrix = Matrix4.diagonal3Values(1, currScale, 1);
      matrix = Matrix4.diagonal3Values(1, currScale, 1)
        ..setTranslationRaw(0, currTrans, 0);
    } else if (index == _currPageValue.floor() - 1) {
      var currScale = 1 - (_currPageValue - index) * (1 - _scaleFactor);
      var currTrans = _height * (1 - currScale) / 2;
      matrix = Matrix4.diagonal3Values(1, currScale, 1);
      matrix = Matrix4.diagonal3Values(1, currScale, 1)
        ..setTranslationRaw(0, currTrans, 0);
    } else {
      var currScale = 0.8;
      matrix = Matrix4.diagonal3Values(1, currScale, 1)
        ..setTranslationRaw(0, _height * (1 - _scaleFactor) / 2, 1);
    }

    // ----------------------------- Slider ------------------------------------
    return Transform(
      transform: matrix,
      child: Stack(
        children: [
          // ------------------------ Image Section ----------------------------
          Container(
            height: Dimensions.pageViewContainer,
            margin: EdgeInsets.only(
                left: Dimensions.width10, right: Dimensions.width10),
            decoration: BoxDecoration(
                borderRadius: BorderRadius.circular(Dimensions.radius30),
                color: const Color(0xFF69c5df),
                image: const DecorationImage(
                    fit: BoxFit.cover,
                    image: AssetImage("assets/images/momo.jpg"))),
          ),

          // ------------------------- Text and Icon Section -------------------
          Align(
            alignment: Alignment.bottomCenter,
            child: Container(
              height: Dimensions.pageViewTextContainer,
              margin: EdgeInsets.only(
                  left: Dimensions.width30,
                  right: Dimensions.width30,
                  bottom: Dimensions.height30),
              decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(Dimensions.radius20),
                  color: Colors.white,
                  boxShadow: const [
                    BoxShadow(
                        color: Color(0xFFe8e8e8),
                        blurRadius: 5.0,
                        offset: Offset(0, 5)),
                    BoxShadow(
                        color: Colors.white,
                        // blurRadius: 5.0,
                        offset: Offset(-5, 0)),
                    BoxShadow(
                        color: Colors.white,
                        // blurRadius: 5.0,
                        offset: Offset(5, 0)),
                  ]),
              child: Container(
                padding: EdgeInsets.only(
                    top: Dimensions.height15, left: 15, right: 15),
                child: Column(crossAxisAlignment: CrossAxisAlignment.start,

                    // ---------------------- Food Title -----------------------
                    children: [
                      BigText(text: "Chicken Momo"),
                      SizedBox(height: Dimensions.height10),

                      // -------------- Rating & comments Section ----------------
                      Row(
                        children: [
                          Wrap(
                              children: List.generate(
                                  5,
                                  (index) => Icon(
                                        Icons.star,
                                        color: AppColor.kSecondaryColor,
                                        size: Dimensions.inconSize24,
                                      ))),
                          const SizedBox(
                            width: 10,
                          ),
                          SmallText(text: '4.5'),
                          const SizedBox(
                            width: 10,
                          ),
                          SmallText(text: '1275'),
                          const SizedBox(
                            width: 10,
                          ),
                          SmallText(text: 'comments'),
                        ],
                      ),
                      SizedBox(
                        height: Dimensions.height20,
                      ),

                      // ----------------- Bottom Icons Section ----------------
                      Row(
                        mainAxisAlignment: MainAxisAlignment.spaceBetween,
                        children: const [
                          IconTextWidget(
                              icon: Icons.thumb_up_outlined,
                              Text: 'Add to cart',
                              iconColor: AppColor.kButtonColor),
                          IconTextWidget(
                            icon: Icons.location_on,
                            Text: '1.7km',
                            iconColor: Colors.blue,
                          ),
                          IconTextWidget(
                              icon: Icons.access_time_rounded,
                              Text: '32 min',
                              iconColor: AppColor.kPrimaryColor),
                        ],
                      ),
                      // SizedBox(
                      //   height: Dimensions.height10,
                      // )
                    ]),
              ),
            ),
          ),
        ],
      ),
    );
  }
  // ============================ Slider Section End ===========================

  // ========================= Catagories Section Start ========================
  Widget _foodList() {
    return SingleChildScrollView(
      scrollDirection: Axis.horizontal,
      child: Container(
        margin: EdgeInsets.only(
            left: Dimensions.width20,
            right: Dimensions.width20,
            bottom: Dimensions.height10),
        child: Padding(
          padding: const EdgeInsets.symmetric(vertical: 15, horizontal: 5),
          child: Row(
            children: [
              for (int i = 0; i < 10; i++)
                Padding(
                  padding: const EdgeInsets.symmetric(horizontal: 10),
                  child: Container(
                    width: 100,
                    height: 100,
                    padding: const EdgeInsets.all(8),
                    decoration: BoxDecoration(
                        color: Colors.white,
                        borderRadius: BorderRadius.circular(10),
                        boxShadow: [
                          BoxShadow(
                            color: Colors.grey.withOpacity(0.5),
                            spreadRadius: 2,
                            blurRadius: 10,
                            offset: const Offset(0, 3),
                          )
                        ],
                        image: const DecorationImage(
                            fit: BoxFit.cover,
                            image: AssetImage(
                              "assets/images/momo.jpg",
                            ))),
                  ),
                ),
            ],
          ),
        ),
      ),
    );
  }
  // =========================== Catagories Section End ========================
}
