import 'package:flutter/material.dart';
import 'package:foodex_app/components/component.dart';
import 'package:foodex_app/components/under_part.dart';
import 'package:foodex_app/constant/constants.dart';
import 'package:foodex_app/model/user.dart';
import 'package:foodex_app/repository/user_repo.dart';
import 'package:foodex_app/utils/dimension.dart';
import 'package:foodex_app/widgets/widgets.dart';
import 'package:motion_toast/motion_toast.dart';
import '../screen.dart';

class LoginScreen extends StatefulWidget {
  const LoginScreen({super.key});

  @override
  State<LoginScreen> createState() => _LoginScreenState();
}

class _LoginScreenState extends State<LoginScreen> {
  final _formKey = GlobalKey<FormState>();
  final _phoneController = TextEditingController();
  final _passwordController = TextEditingController();

  _loginUser() async {
    int phoneNumber = int.parse(_phoneController.text);
    String password = _passwordController.text;

    User? userData =
        await UserRepositoryImpl().loginUser(phoneNumber, password);
    if (userData != null) {
      _showMessage(1);
    }
  }

  _showMessage(int status) {
    if (status > 0) {
      MotionToast.success(
        description: const Text('Successfully Register'),
        onClose: () {
          Navigator.of(context).push(
            MaterialPageRoute(
              builder: (_) => const DashBoardScreen(),
            ),
          );
        },
      ).show(context);
    } else {
      MotionToast.error(description: const Text('Fail to register'))
          .show(context);
    }
  }

  @override
  Widget build(BuildContext context) {
    Size size = MediaQuery.of(context).size;
    return Scaffold(
        body: SingleChildScrollView(
      child: SafeArea(
        child: SizedBox(
          width: size.width,
          height: size.height,
          child: SingleChildScrollView(
            child: Stack(children: [
              const Upside(imgUrl: "assets/images/login_logo.png"),
              const PageTitleBar(
                title: 'Welcome',
              ),
              Padding(
                padding: EdgeInsets.only(top: Dimensions.height300),
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.center,
                  children: [
                    const Text(
                      "Please Login with your account to continue",
                      textAlign: TextAlign.center,
                      style: TextStyle(
                          color: Colors.black,
                          fontFamily: 'OpenSans',
                          fontSize: 17,
                          fontWeight: FontWeight.normal),
                    ),
                    const SizedBox(
                      height: 20,
                    ),
                    Form(
                        autovalidateMode: AutovalidateMode.always,
                        key: _formKey,
                        child: Column(
                          children: [
                            UserInputField(
                              controller: _phoneController,
                              hintText: 'Enter Phone number',
                              prefixIcon: Icons.person,
                              validator: (value) {
                                if (value!.isEmpty ||
                                    !RegExp(r'^[0-9]{10}$').hasMatch(value)) {
                                  return 'Enter valid phone number';
                                }
                                return null;
                              },
                            ),
                            const SizedBox(
                              height: 20,
                            ),
                            PasswordField(
                              controller: _passwordController,
                            ),
                            const SizedBox(
                              height: 20,
                            ),
                            // switchListTile(),
                            const Text(
                              'Forgot password?',
                              style: TextStyle(
                                  color: AppColor.kSecondaryColor,
                                  fontFamily: 'OpenSans',
                                  fontWeight: FontWeight.w600,
                                  fontSize: 15),
                            ),
                            const SizedBox(
                              height: 20,
                            ),
                            PressButton(
                              text: 'login',
                              press: () {
                                if (_formKey.currentState!.validate()) {
                                  _loginUser();
                                }
                              },
                            ),
                            const SizedBox(height: 10),
                            const Text(
                              "Or",
                              style: TextStyle(
                                  color: Colors.black,
                                  fontFamily: 'OpenSans',
                                  fontSize: 15,
                                  fontWeight: FontWeight.w600),
                            ),
                            const SizedBox(height: 10),
                            iconButton(context),
                            const SizedBox(
                              height: 20,
                            ),
                            UnderPart(
                                title: "Don't have an account?",
                                navigatorText: "Sign Up",
                                onTap: () {
                                  Navigator.push(
                                      context,
                                      MaterialPageRoute(
                                          builder: (context) =>
                                              const SignUpScreen()));
                                }),
                            const SizedBox(
                              height: 40,
                            ),
                          ],
                        ))
                  ],
                ),
              )
            ]),
          ),
        ),
      ),
    ));
  }
}

