import 'package:flutter/material.dart';
import 'package:foodex_app/components/component.dart';
import 'package:foodex_app/components/under_part.dart';
import 'package:foodex_app/model/model.dart';
import 'package:foodex_app/repository/user_repo.dart';
import 'package:foodex_app/utils/dimension.dart';
import 'package:foodex_app/widgets/widgets.dart';
import 'package:motion_toast/motion_toast.dart';

import '../screen.dart';

class SignUpScreen extends StatefulWidget {
  const SignUpScreen({super.key});

  @override
  State<SignUpScreen> createState() => _SignUpScreenState();
}

class _SignUpScreenState extends State<SignUpScreen> {
  final _formKey = GlobalKey<FormState>();

  final _emailController = TextEditingController();
  final _phoneController = TextEditingController();
  final _passwordController = TextEditingController();

  _saveUser() async {
    User user = User(_emailController.text, int.parse(_phoneController.text),
        _passwordController.text);

    int status = await UserRepositoryImpl().addUser(user);
    _showMessage(status);
  }

  _showMessage(int status) {
    if (status > 0) {
      MotionToast.success(
        description: const Text('Successfully Register'),
        onClose: () {
          Navigator.of(context).push(
            MaterialPageRoute(
              builder: (_) => const VerificationScreen(),
            ),
          );
        },
      ).show(context);
    } else {
      MotionToast.error(description: const Text('Fail to register'))
          .show(context);
    }
  }

  @override
  Widget build(BuildContext context) {
    Size size = MediaQuery.of(context).size;
    return SafeArea(
      child: Scaffold(
          body: SizedBox(
        width: size.width,
        height: size.height,
        child: SingleChildScrollView(
          child: Stack(children: [
            const Upside(imgUrl: "assets/images/login_logo.png"),
            const PageTitleBar(
              title: 'Sign Up',
            ),
            Padding(
              padding: EdgeInsets.only(top: Dimensions.height300),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.center,
                children: [
                  const Text(
                    "Please register to get started",
                    textAlign: TextAlign.center,
                    style: TextStyle(
                        color: Colors.black,
                        fontFamily: 'OpenSans',
                        fontSize: 17,
                        fontWeight: FontWeight.normal),
                  ),
                  const SizedBox(
                    height: 15,
                  ),
                  Form(
                      autovalidateMode: AutovalidateMode.always,
                      key: _formKey,
                      child: Column(
                        children: [
                          UserInputField(
                            controller: _emailController,
                            hintText: 'Enter your Email',
                            prefixIcon: Icons.email,
                            validator: (value) {
                              if (value!.isEmpty ||
                                  !RegExp(r'^[\w-\.]+@([\w-]+\.)+[\w]{2,4}')
                                      .hasMatch(value)) {
                                return 'Enter your email';
                              }
                              return null;
                            },
                          ),
                          const SizedBox(
                            height: 15,
                          ),
                          UserInputField(
                            controller: _phoneController,
                            hintText: 'Enter your Phone number',
                            prefixIcon: Icons.phone,
                            validator: (value) {
                              if (value!.isEmpty ||
                                  !RegExp(r'^[0-9]{10}$').hasMatch(value)) {
                                return 'Enter your phone number';
                              }
                              return null;
                            },
                          ),
                          const SizedBox(
                            height: 15,
                          ),
                          PasswordField(
                            controller: _passwordController,
                          ),
                          // switchListTile(),
                          const SizedBox(
                            height: 4,
                          ),
                          PressButton(
                            text: 'Sign up',
                            press: () {
                              if (_formKey.currentState!.validate()) {
                                _saveUser();
                              }
                            },
                          ),
                          const SizedBox(height: 10),
                          const Text(
                            "Or",
                            style: TextStyle(
                                color: Colors.black,
                                fontFamily: 'OpenSans',
                                fontSize: 15,
                                fontWeight: FontWeight.w600),
                          ),
                          const SizedBox(height: 10),
                          iconButton(context),
                          const SizedBox(
                            height: 20,
                          ),
                          UnderPart(
                              title: "Already have account?",
                              navigatorText: "Log in",
                              onTap: () {
                                Navigator.push(
                                    context,
                                    MaterialPageRoute(
                                        builder: (context) =>
                                            const LoginScreen()));
                              }),
                          const SizedBox(
                            height: 40,
                          ),
                        ],
                      ))
                ],
              ),
            )
          ]),
        ),
      )),
    );
  }
}

