import 'package:flutter/material.dart';
import 'package:foodex_app/constant/constants.dart';
import 'package:foodex_app/utils/dimension.dart';

class UserInputField extends StatefulWidget {
  const UserInputField({
    Key? key,
    this.hintText,
    // this.icon = Icons.person,
    this.prefixIcon,
    this.validator,
    this.errorText,
    this.controller,
  }) : super(key: key);
  final String? hintText;
  final IconData? prefixIcon;
  final String? Function(String?)? validator;
  final String? errorText;
  final controller;

  @override
  State<UserInputField> createState() => _UserInputFieldState();
}

class _UserInputFieldState extends State<UserInputField> {
  @override
  Widget build(BuildContext context) {
    Size size = MediaQuery.of(context).size;
    return Container(
      width: size.width * 0.8,
      decoration: BoxDecoration(
        borderRadius: BorderRadius.circular(Dimensions.radius30),
      ),
      child: TextFormField(
        cursorColor: Colors.black12,
        validator: widget.validator,
        controller: widget.controller,
        decoration: InputDecoration(
            contentPadding: const EdgeInsets.symmetric(vertical: 22),
            enabledBorder: UnderlineInputBorder(
              borderSide: const BorderSide(
                color: AppColor.kSecondaryBgColor,
              ),
              borderRadius: BorderRadius.circular(Dimensions.radius30),
            ),
            filled: true,
            fillColor: AppColor.kSecondaryBgColor,
            focusedBorder: OutlineInputBorder(
                borderSide: const BorderSide(color: AppColor.kSecondaryBgColor),
                borderRadius: BorderRadius.circular(30)),
            errorText: widget.validator != null ? null : widget.errorText,
            errorBorder: const OutlineInputBorder(
                borderSide: BorderSide(color: Colors.red),
                borderRadius: BorderRadius.all(Radius.circular(30))),
            focusedErrorBorder: OutlineInputBorder(
                borderSide: const BorderSide(color: Colors.red),
                borderRadius: BorderRadius.circular(30)),
            prefixIcon: null == widget.prefixIcon
                ? null
                : Icon(
                    widget.prefixIcon,
                    color: Colors.black,
                    // size:Dimensions.icon20,
                  ),
            hintText: widget.hintText,
            hintStyle: const TextStyle(fontFamily: 'OpenSans'),
            border: InputBorder.none),
      ),
    );
  }
}
